const openMenuBtn=document.querySelector(".main-item"),closeMenuBtn=document.querySelector(".close-btn"),menu=document.querySelector(".sub-menu"),linkItem=document.querySelectorAll(".menu-link__item"),reloader=document.querySelector(".header__logo");function disableScrolling(){let e=window.scrollX,n=window.scrollY;window.onscroll=function(){window.scrollTo(e,n)}}function enableScrolling(){window.onscroll=function(){}}openMenuBtn.addEventListener("click",()=>{menu.classList.add("menu--active")}),closeMenuBtn.addEventListener("click",()=>{menu.classList.remove("menu--active")});for(let e=0;e<linkItem.length;e++)linkItem[e].addEventListener("click",()=>{menu.classList.remove("menu--active")});reloader.addEventListener("click",()=>{window.location.reload()});